# ╔═╡ 349fb7c9-8fc8-4554-8fdc-26604b2d3920
function read_input(input)
	pos = (0,0)
	count_by_position = Dict(pos => 1)
	for c in input 
		if c == '^'
			pos = (pos[1], pos[2] - 1)
		elseif c == 'v'
			pos = (pos[1], pos[2] + 1)
		elseif c == '<'
			pos = (pos[1] - 1, pos[2])
		elseif c == '>'
			pos = (pos[1] + 1, pos[2])
		end
		
		if haskey(count_by_position, pos)
			count_by_position[pos] += 1
		else
			count_by_position[pos] = 1
		end
	end
	count_by_position
end

# ╔═╡ 08c02233-d5ab-4401-aec8-7e659d5778d1
grid1 = read_input(puzzle_input)

# ╔═╡ 3ffcafa7-fdcd-4e5a-8b7b-7b562bdb7e59
part1 = length(filter(x -> x > 0, collect(values(grid1))))

